package com.fr.design.gui.itable;import javax.swing.AbstractCellEditor;import javax.swing.table.TableCellEditor;/*** Created by IntelliJ IDEA.* Author : Richer* Version: 6.5.6* Date: 12-12-13* Time: 下午10:20*/public abstract class UITableEditor extends AbstractCellEditor implements TableCellEditor {	private Object value;	public void setValue(Object value) {		this.value = value;	}	/**	 * Returns the value contained in the editor.	 *	 * @return the value contained in the editor	 */	@Override	public Object getCellEditorValue() {		return value;	}}